import express from 'express';
// https://stackoverflow.com/questions/43952198/how-to-override-or-extend-a-libary-type-definition-in-typescript
declare module 'express' {
    interface Request {
        query: {
            /** Meta type to be queries against; relative to the `meta` table in model */
            metatype?: 'artist_name' | 'category_name';
            /** When querying for specific meta types, these should be passed as valid query strings */
            artist?: string;
            category?: string;
            /** Query for specific media types */
            mediatype?: 'V' | 'A' | 'I';
            /** Pagination options */
            limit?: number;
            page?: number;
            // maintitle?: string;
            // subtitle?: string;
            /** A specific file to be fetched from the server */
            // file?: string;
            /** The `_guid` field to be requested for a fetch/query/insert */
            id?: string;
        }
    }
    // interface Locals extends HonkServer.ApplicationConfig { };
}
